    },[]);

    return(
        <div>
            <h1>List of all Events</h1>
            <ul>
                {events.map(event => (
                    <li key={event._id}>
                       <h2>{event.title}</h2>
                       <p>{event.date}</p>
                       <p>{event.time}</p>
                       <p>{event.location}</p>
                       <p>{event.description}</p>
