import { useState,useEffect} from 'react'
import axios from 'axios'
function UserSignupForm(){
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [role, setRole] = useState('');
    const [password, setPassword] = useState('');

    const handleChangesubmit =(e)=>{
        e.prevent.default()
    }
    useEffect(()=>{
        let paylod = {name,email,role,password}
        axios.post("https://localhost/api/create",paylod)
        .then((response)=>{
            localStorage.setItem('token')
            console.log(response.data)
            console.log(response.data.token)
        }).catch(()=>{
            console.log('user not created')
        })
    },)
    return(
        <div>
            <form onSubmit={handleChangesubmit}>
                <table>
                    <tbody>
                    <tr>
                        <td><label>Name:</label></td>
                        <td><input type='text'value={name} onChange={e => setName(e.target.value)} placeholder='Enter your name'></input></td>
                    </tr>
                    
                    <tr>
                        <td><label>Email:</label></td>
                        <td><input type='text'value={email} onChange={e => setEmail(e.target.value)} placeholder='Enter your Email'></input></td>
                    </tr>

                    <tr>
                        <td><label>Role: </label></td>
                        <td><input type='text'value={role} onChange={e=> setRole(e.target.value)} placeholder='Enter your role'></input></td>
                    </tr>

                    <tr>
                        <td><label>Password:</label></td>
                           <td><input type='text'value={password} onChange={e=> setPassword(e.target.value)} placeholder='Enter your password'></input></td>
                    </tr>

                    <tr>
                        <td><button type='submit'>CreateUser</button></td>
                    </tr>
                    </tbody>
                </table>
            </form>
        </div>
    )
}
export default UserSignupForm;
