import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function EventList() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4580/api/readEvent')
      .then((response) => {
        setEvents(response.data.data);
      }).catch((error) => {
        console.error('Error getting EventList', error);
      });
  }, []);
  const deleteEvent = async (id) => {
    try {
      await axios.delete('http://localhost:4580/api/deleteEvent/'+id);
      setEvents(events.filter(event => event._id !== id));
    } catch (error) {
      console.error('Error deleting event', error);
    }
  };
  return (
    <div>
      <h1>List of all Events</h1>
      <table>
        <tr>
          <th>Title</th>
          <th>Date</th>
          <th>Time</th>
          <th>Location</th>
          <th>Description</th>
        </tr>
     
      <tr>
        {events.map(event => (
          <td key={event._id}>
           <tr> <td><h2>{event.title}</h2></td></tr>
            <tr><td><p>{event.date}</p></td></tr>
            <td><p>{event.time}</p></td>
            <td><p>{event.location}</p></td>
            <td><p>{event.description}</p></td>
            <Link to={`/EditEvent/${event._id}`}><button>Edit</button></Link>
            <button onClick={() => deleteEvent(event._id)}>Delete</button>
          </td>
        ))}
      </tr>
      </table>
    </div>
  );
}

export default EventList;
