import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from '../components/event.module.css';

function EventList() {
  const [events, setEvents] = useState([]);
  const [eventData, setEventData] = useState({
    title: '',
    date: '',
    time: '',
    location: '',
    description: ''
  });

  useEffect(() => {
    axios.get('http://localhost:4580/api/getAllEvents')
      .then(response => {
        setEvents(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  }, []);

  const deleteEventById = async (id) => {
    try {
      await axios.delete(`http://localhost:4580/api/deleteEvent/${id}`);
      setEvents(events.filter(event => event._id !== id));
      console.log(`Event with id ${id} deleted successfully.`);
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const updateEvent = async (event) => {
    try {
      const response = await axios.put(
        `http://localhost:4580/api/updateEvent/${event._id}`,
        event
      );
      setEvents(events.map(e => (e._id === event._id ? response.data.data : e)));
      setEventData({
        title: '',
        date: '',
        time: '',
        location: '',
        description: ''
      }); // Close the edit form
      console.log('Event updated successfully:', response.data.data);
    } catch (error) {
      console.log('Error updating event', error);
    }
  };

  const handleEdit = (event) => {
    setEventData(event);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateEvent(eventData);
  };

  return (
    <div id={styles.profile}>
      <h1>List of All Events</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Date</th>
            <th>Time</th>
            <th>Location</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map(event => (
            <tr key={event._id}>
              <td>{event.title}</td>
              <td>{new Date(event.date).toLocaleDateString()}</td>
              <td>{event.time}</td>
              <td>{event.location}</td>
              <td>{event.description}</td>
              <td>
                <button onClick={() => handleEdit(event)}>Edit</button>
                <button onClick={() => deleteEventById(event._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {eventData && (
        <form onSubmit={handleSubmit}>
          <h2>Edit Event</h2>
          <input
            type="text"
            value={eventData.title || ''}
            onChange={(e) => setEventData({ ...eventData, title: e.target.value })}
            placeholder="Enter your title"
          />
          <input
            type="date"
            value={eventData.date ? eventData.date.split('T')[0] : ''}
            onChange={(e) => setEventData({ ...eventData, date: e.target.value })}
            placeholder="Enter your date"
          />
          <input
            type="time"
            value={eventData.time || ''}
            onChange={(e) => setEventData({ ...eventData, time: e.target.value })}
            placeholder="Enter your time"
          />
          <input
            type="text"
            value={eventData.location || ''}
            onChange={(e) => setEventData({ ...eventData, location: e.target.value })}
            placeholder="Enter your location"
          />
          <input
            type="text"
            value={eventData.description || ''}
            onChange={(e) => setEventData({ ...eventData, description: e.target.value })}
            placeholder="Enter your description"
          />
          <button type="submit">Update Event</button>
          <button type="button" onClick={() => setEventData({
            title: '',
            date: '',
            time: '',
            location: '',
            description: ''
          })}>Cancel</button>
        </form>
      )}
    </div>
  );
}

export default EventList;
