import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function EventList() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4580/api/readEvent')
      .then(response => {
        setEvents(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  }, []);

    useEffect(()=>{
      const deleteEvent = (id) => {
        axios.delete(`http://localhost:4580/api/deleteEvent/${id}`)
          .then(response => {
            setEvents(events.filter(event => event._id !== id));
            console.log(response.data.data)
          })
          .catch(error => {
            console.error('Error deleting event:', error);
          });
      }
      deleteEvent();
    },[])
    
  return (
    <div>
      <h1>List of All Events</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Date</th>
            <th>Time</th>
            <th>Location</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map(event => (
            <tr key={event._id}>
              <td>{event.title}</td>
              <td>{event.date}</td>
              <td>{event.time}</td>
              <td>{event.location}</td>
              <td>{event.description}</td>
              <td>
                <Link to={`/editEvent/${event._id}`}><button>Edit</button></Link>
              </td>
              <td><button onClick={(de) => deleteEvent(event._id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EventList;
