import { useEffect, useState } from "react";
import axios from "axios";

function EventList () {

    const [events, setEvents] = useState([]);

    useEffect(()=>{
       const fetchevents = async()=>{
        try{
            const response = await axios.get('http://localhost:4580/api/readEvent')
            const data = await response.json([]);
            setEvents(data)
        }catch(error){
            console.error(error)
        }
       }
        fetchevents();
    },[])

    return(
        <div>
            <h1>List of all Events</h1>
            {events.map((event,index)=>(
                <div key={index}>
                    <h2>{event.title}</h2>
                       <p>{event.date}</p>
                       <p>{event.time}</p>
                       <p>{event.location}</p>
                       <p>{event.description}</p>
                       <button>Edit</button>
                </div>
            ))}
        </div>
    )
}

export default EventList;
