import React, { useEffect, useState } from 'react';
import { Calendar, dayjsLocalizer } from 'react-big-calendar';
import dayjs from 'dayjs';
import axios from 'axios';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = dayjsLocalizer(dayjs);

const EventCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4580/api/getAllEvents')
      .then(response => {
        console.log('Fetched Events:', response.data.data);
        const mappedEvents = response.data.data.map(event => {
          const startDateTime = dayjs(`${event.date}T${event.time}`).toDate();
          const endDateTime = dayjs(startDateTime).add(1, 'hour').toDate(); // Assuming 1 hour duration

          return {
            id: event._id,
            title: event.title,
            start: startDateTime,
            end: endDateTime,
            location: event.location,
            description: event.description,
          };
        });
        console.log('Mapped Events:', mappedEvents);
        setEvents(mappedEvents);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  }, []);

  return (
    <div style={{ height: '100vh' }}>
      <h1>Event Calendar</h1>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: '90vh' }}
      />
    </div>
  );
};

export default EventCalendar;
