import axios from "axios";
import { useEffect, useState } from "react";

function EventList() {
  const [events, setEvents] = useState([]);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:4580/api/readEvent');
      if (Array.isArray(response.data)) {
        setEvents(response.data);
      } else {
        setEvents([response.data]); // wrap the object in an array
      }
    } catch (error) {
      console.log('error fetchingEvents', error);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  return (
    <div>
      <h1>List of all Events</h1>
      {events.map((event, index) => (
        <div key={index}>
          <h2>{event.title}</h2>
          <p>{event.date}</p>
          <p>{event.time}</p>
          <p>{event.location}</p>
          <p>{event.description}</p>
          <button>Edit</button>
        </div>
      ))}
    </div>
  );
}

export default EventList;
