import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function EventList() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4580/api/readEvent')
      .then((response) => {
        setEvents(response.data.data)
      }).catch((error) => {
        console.error('error getting EvenList', error)
      })
  }, [])

  const updateEvent = async (event) => {
    try {
      const response = await axios.put(
        `http://localhost:4580/api/updateEvent/${event._id}`,
        event
      );
      setEvents(response.data.data);
      console.log("Event updated successfully:", response.data.data);
    } catch (error) {
      console.log("Error updating event", error);
    }
  };
  return (
    <div>
      <h1>List of all Events</h1>
      <ul>
        {events.map(event => (
          <li key={event._id}>
            <h2>{event.title}</h2>
            <p>{event.date}</p>
            <p>{event.time}</p>
            <p>{event.location}</p>
            <p>{event.description}</p>
            <Link to={`/edit-event/${event._id}`}>Edit Event</Link>
            
          </li>
        ))}
      </ul>
    </div>
  )
}

export default EventList;
