import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from '../components/event.module.css';

function EventList() {
  const [events, setEvents] = useState([]);
  const [eventData, setEventData] = useState({
    _id: '',
    title: '',
    date: '',
    time: '',
    location: '',
    description: ''
  });
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:4580/api/getAllEvents')
      .then(response => {
        setEvents(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  }, []);

  const deleteEventById = async (id) => {
    try {
      await axios.delete(`http://localhost:4580/api/deleteEvent/${id}`);
      setEvents(events.filter(event => event._id !== id));
      console.log(`Event with id ${id} deleted successfully.`);
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const updateEvent = async () => {
    try {
      const response = await axios.put(
        `http://localhost:4580/api/updateEvent/${eventData._id}`,
        eventData
      );
      setEvents(events.map(e => (e._id === eventData._id ? response.data.data : e)));
      setEventData({
        _id: '',
        title: '',
        date: '',
        time: '',
        location: '',
        description: ''
      });
      setEditMode(false);
      console.log('Event updated successfully:', response.data.data);
    } catch (error) {
      console.log('Error updating event', error);
    }
  };

  const handleEdit = (event) => {
    setEventData(event);
    setEditMode(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (eventData) {
      updateEvent();
    }
  };

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  return (
    <div id={styles.profile}>
      <h1>List of All Events</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Date</th>
            <th>Time</th>
            <th>Location</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map(event => (
            <tr key={event._id}>
              <td>{event.title}</td>
              <td>{new Date(event.date).toLocaleDateString()}</td>
              <td>{event.time}</td>
              <td>{event.location}</td>
              <td>{event.description}</td>
              <td>
                <button onClick={() => handleEdit(event)}>Edit</button>
                <button onClick={() => deleteEventById(event._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editMode && (
        <form onSubmit={handleSubmit}>
          <h2>Edit Event</h2>
          <input
            type="text"
            name="title"
            value={eventData.title}
            onChange={handleChange}
            placeholder="Enter your title"
          />
          <input
            type="date"
            name="date"
            value={eventData.date.split('T')[0]}
            onChange={handleChange}
            placeholder="Enter your date"
          />
          <input
            type="time"
            name="time"
            value={eventData.time}
            onChange={handleChange}
            placeholder="Enter your time"
          />
          <input
            type="text"
            name="location"
            value={eventData.location}
            onChange={handleChange}
            placeholder="Enter your location"
          />
          <input
            type="text"
            name="description"
            value={eventData.description}
            onChange={handleChange}
            placeholder="Enter your description"
          />
          <button type="submit">Update Event</button>
          <button type="button" onClick={() => { 
            setEventData({
              _id: '',
              title: '',
              date: '',
              time: '',
              location: '',
              description: ''
            }); 
            setEditMode(false); 
          }}>Cancel</button>
        </form>
      )}
    </div>
  );
}

export default EventList;
