import React, { useEffect, useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import axios from 'axios';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import enUS from 'date-fns/locale/en-US';
import 'react-big-calendar/lib/css/react-big-calendar.css';

// Setup date-fns localizer
const locales = { 'en-US': enUS };
const localizer = dateFnsLocalizer({ format, parse, startOfWeek, getDay, locales });

const EventCalendar = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4580/api/getAllEvents')
      .then(response => {
        console.log('Fetched Events:', response.data.data);
        const mappedEvents = response.data.data.map(event => {
          const startDateTime = new Date(`${event.date}T${event.time}`);
          const endDateTime = new Date(startDateTime.getTime() + 60 * 60 * 1000); // Assuming 1 hour duration
          
          return {
            id: event._id,
            title: event.title,
            start: startDateTime,
            end: endDateTime,
            location: event.location,
            description: event.description,
          };
        });
        console.log('Mapped Events:', mappedEvents);
        setEvents(mappedEvents);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  }, []);

  return (
    <div style={{ height: '100vh' }}>
      <h1>Event Calendar</h1>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: '90vh' }}
      />
    </div>
  );
};

export default EventCalendar;
