import axios from "axios";
import { useEffect, useState } from "react";
// import { Link } from "react-router-dom";


function EventList () {

    const [events, setEvents] = useState([]);

    useEffect(()=>{
        axios.get('http://localhost:4580/api/readEvent')
        .then((response)=>{
            setEvents(response.data.data)
        }).catch((error)=>{
            console.error('error getting EvenList',error)
        })
    },[])

    

    const updateEvent = async (event) => {
        try {
          const response = await axios.put(
            `http://localhost:4580/api/updateEvent/${event._id}`
          );
          setEvents(response.data.data);
          console.log("user updated successfully:", response.data.data);
        } catch (error) {
          console.log("Error updating user", error);
        }
      };
    
      useEffect(() => {
        updateEvent();
    },[]);

    return(
        <div>
            <h1>List of all Events</h1>
            <ul>
                {events.map(event => (
                    <li key={event._id}>
                       <h2>{event.title}</h2>
                       <p>{event.date}</p>
                       <p>{event.time}</p>
                       <p>{event.location}</p>
                       <p>{event.description}</p>
                       {/* <Link to ='/EditEvent/${event._id}'><button>Edit</button></Link> */}
                       <button onClick={()=>updateEvent(event)}>Edit</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default EventList;
